AddEventHandler('esx_nightclubjob:bossnightclub', function()
    if ESX.PlayerData.job and ESX.PlayerData.job.name == 'nightclub' then
    TriggerEvent('esx_society:openBossMenu', 'nightclub', function(data, menu)
    end, {wash = false})
    end
end)